import React from "react";

// http://localhost:3000/events/1/event_images/
// http://localhost:3000/events/1/attendees/count
// http://localhost:3000/events/6/attendees
// PUT	/organizers/:id

const ShowEvent = props => {
  return (
    <div>
      <div
        className="back"
        onClick={() => {
          props.setCurrentEvent(null);
        }}
      >
        back
      </div>
      <div className="container">
        <div className="event">
          <div>
            <h2>{props.activeEvent.name}</h2>
          </div>
          <div>
            <img src={props.activeEvent.logo} alt="" />
            <img src={props.activeEvent.logo} alt="" />
            <div className="event-details">
            <p>{props.activeEvent.description}</p>
            <p>{props.activeEvent.start_date}</p>
            <p>{props.activeEvent.end_date}</p>
            <p>{props.activeEvent.location}</p>
            <p>{props.activeEvent.location_id}</p>
            <p>{props.activeEvent.image_url}</p>
              <div className="event-buttons">
                <button onClick={() => {props.toggleModal()}}>Edit</button>
                <button onClick={() => {props.deleteEvent(props.activeEvent.id)}}>Delete</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowEvent;
