import React, { Component } from "react";
import { getToken } from "../services/authService";
import Event from './Event'

class Organizer extends Component {
  constructor() {
    super();
    this.state = {
        events:[]
    };
  }

    componentDidMount(){
        console.log('fetching data');
        // const url = 
        // console.log('ttt',getToken())
        // console.log('uu',this.props.user.id)
        fetch(`http://localhost:3000/organizers/${this.props.user.id}/events?auth_token=${getToken()}`)
          .then( response => response.json())
          .then( data => {

            // console.log('bye',JSON.stringify(data));
            // let d=data.toString();
            console.log('dddd',data);
            this.setState({
              events: data
            })
            console.log('ev', this.state.events)
          })
          .catch( error => {
            console.log(error)
          })
      }


      renderEvents(allMeals) {
        return allMeals.map((meal) => {
          return (
            <Event key={meal.id}
              meal={meal}
           setCurrentMeal={this.setCurrentMeal.bind(this)}
            deleteMeal={this.deleteMeal.bind(this)}
            isBaker={this.props.isBaker}
              />
          )
        })
      }


  render() {
    return (
      <div>
  {this.renderTiles(this.state.events)}
        </div>

    );
  }
}

export default Organizer;
