import React, { Component } from "react";
import { getToken } from "../services/authService";
import Event from './Event'

class Organizer extends Component {
  constructor() {
    super();
    this.state = {
        events:[],
        activeEvent: null
    };
  }
    componentDidMount(){
        console.log('fetching data');

        fetch(`http://localhost:3000/organizers/${this.props.user.id}/events?auth_token=${getToken()}`)
          .then( response => response.json())
          .then( data => {
            console.log('dddd',data);
            this.setState({
              events: data
            })
            console.log('ev', this.state.events)
          })
          .catch( error => {
            console.log(error)
          })
      }

      setCurrentEvent(event) {
        console.log('setting event');
        console.log(event);
        this.setState({
          activeEvent: event
        })
      }

      deleteEvent(id) {
        const url = `http://localhost:3000/events/${id}`;
        fetch(url, {
            method: 'DELETE'
          })
          .then(response => response.json())
          .then(data => {
            const updatedEvents = this.state.events.filter( event => event.id !== id)
            this.setState({
              events: updatedEvents,
              activeEvent: null
            })
          })
          .catch(error => {
            console.log(error);
          })
      }


  updateEvent(event) {
    const url = `http://localhost:3000/events/${event.id}`
    fetch(url, {
      method: 'PUT',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(event)
    })
    .then(response => response.json())
    .then(data => {

      const updatedEvents = this.state.events.map(el => {
        return el.id === data.id ? data : el
      })
      console.log('current state: ', this.state.events);
      console.log('new state: ', updatedEvents)

      this.setState({
        shows: updatedEvents,
        activeShow: event
      })
    })
    .catch(error => {
      console.log(error);
    })
  }

  createNewEvent(event) {
   const url = 'http://localhost:3000/events'
   fetch(url, {
       method: 'POST',
       headers: {
         "Content-Type": "application/json"
       },
       body: JSON.stringify(event)
     })
     .then(response => response.json())
     .then(data => {
       console.log('DATA')
       console.log(data);
       const updatedEvents = this.state.events.concat([data]);
       console.log(updatedEvents)
       this.setState({
         events: updatedEvents,
         activeEvent: data
       })
     })
     .catch(error => {
       console.log(error);
     })
  }


      renderEvents(allEvents) {
        return allEvents.map((event) => {
          return (
            <Event key={event.id}
              event={event}
              setCurrentEvent={this.setCurrentEvent.bind(this)}
              />
          )
        })
      }

      renderContent(){
        if (this.state.activeEvent)
        <ShowEvent
        setCurrentEvent={this.setCurrentEvent.bind(this)} 
        activeEvent={this.state.activeEvent}
        deleteEvent={this.deleteEvent.bind(this)}
        // toggleModal={this.toggleModal.bind(this)}
        />

        else
        {this.renderEvents(this.state.events)} 
      }


  render() {
    return (
      <div>

        </div>

    );
  }
}

export default Organizer;
